sorter.formulaText:

if (task1.start<task2.start) return -1;
else if (task1.start>task2.start) return 1;
else return 0;


if (task1.end<task2.end) return -1;
else if (task1.end>task2.end) return 1;
else return 0;


if (task1.duration>task2.duration) return -1;
else if (task1.duration<task2.duration) return 1;
else return 0;


if (task1.cost<task2.cost) return -1;
else if (task1.cost>task2.cost) return 1;
else return 0;


boolean overbudget1=!task1.isAssignment() && (task1.getCost() > task1.getBaselineCost());
boolean overbudget2=!task2.isAssignment() && (task2.getCost() > task2.getBaselineCost());
if (overbudget1 && !overbudget2) return -1;
else if (overbudget2 && !overbudget1) return 1;
else return 0;


boolean complete1=task1.getPercentComplete()==1;
boolean complete2=task2.getPercentComplete()==1;
if (complete2 && !complete1) return -1;
else if (complete1 && !complete2) return 1;
else return 0;


boolean critical1=task1.isCritical();
boolean critical2=task2.isCritical();
if (critical1 && !critical2) return -1;
else if (critical2 && !critical1) return 1;
else return 0;


if (resource1.costPerUse<resource2.costPerUse) return -1;
else if (resource1.costPerUse>resource2.costPerUse) return 1;
else return 0;


if (resource1.standardRate<resource2.standardRate) return -1;
else if (resource1.standardRate>resource2.standardRate) return 1;
else return 0;


if (resource1.overtimeRate<resource2.overtimeRate) return -1;
else if (resource1.overtimeRate>resource2.overtimeRate) return 1;
else return 0;


if (task1.duration<task2.duration) return -1;
else if (task1.duration>task2.duration) return 1;
else return 0;


if (task1.cost<task2.cost) return -1;
else if (task1.cost>task2.cost) return 1;
else return 0;
